import React, { useState, useEffect } from 'react'

import Card from 'antd/lib/card'
import Empty from 'antd/lib/empty'
import Input from 'antd/lib/input'
const { Search } = Input

const SearchFilterMap = (item, searchParam) => {
    if (item.length === 0){
        return(
            <Card.Grid>
                <Empty/>
            </Card.Grid>
        )
    }
    
    return item.map((item, index) => {
        if((item.title.toLowerCase()).includes(searchParam.toLowerCase())) {
            return (
                <Card.Grid key={index}>
                    <img style={{width:"100%"}}alt="example" src={item.image} />
                    <p><strong>{item.title}</strong></p>
                    <p>{"$" + item.price.toFixed(2)}</p>
                    <p>{item.category.charAt(0).toUpperCase() + item.slice(1)}</p>
                    <p>{item.description}</p>
                </Card.Grid>
            )
        } else {
            return null
        }
    })
}


export default function Home() {
    const [product, setProduct] = useState([])
    const [searchParam, setParam] = useState('')
    useEffect(() => {
        fetch('https://fakestoreapi.com/products/')
            .then(res => res.json())
            .then((productsArray) => {
                setProduct(productsArray)
            })
    }, [])

    const onSearch = value => setParam(value);

    return (
        <main className="home">
            <section>
                <p className="title">Fake Store Assessment</p>
                <div className="card">
                    <Search className="search-bar" placeholder="input search text" onSearch={onSearch} />
                    <Card className="card-grid" title="Card Title">
                        {SearchFilterMap(product, searchParam)}
                    </Card>
                </div>

            </section>
        </main>
    )
}
