import React, { useState, useEffect } from 'react'

import Card from 'antd/lib/card'
import Empty from 'antd/lib/empty'
import Input from 'antd/lib/input'
import Form from 'antd/lib/form'
import Button from 'antd/lib/button'
const { Search } = Input

const SearchFilterMap = (item, searchParam) => {
    if (item.length === 0){
        return(
            <Card.Grid>
                <Empty/>
            </Card.Grid>
        )
    }
    
    return item.map((item, index) => {
        if((item.title.toLowerCase()).includes(searchParam.toLowerCase())) {
            return (
                <Card.Grid key={index}>
                    <img style={{width:"100%"}}alt="example" src={item.image} />
                    <p><strong>{item.title}</strong></p>
                    <p>{"$" + item.price.toFixed(2)}</p> {/* Integer must have 2 decimal spaces to look better */}
                    <p>{item.category.charAt(0).toUpperCase() + item.category.slice(1)}</p> {/* Capitalize First Letter */}
                    <p>{item.description}</p>
                </Card.Grid>
            )
        } else {
            return null
        }
    })
}

export default function Home() {
    const [ product, setProduct ] = useState([])
    const [ searchParam, setParam ] = useState('')
    
    useEffect(() => {
        fetch('https://fakestoreapi.com/products/')
            .then(res => res.json())
            .then((productsArray) => {
                setProduct(productsArray)
            })
    }, [])

    const onSearch = value => setParam(value);
    const onFinish = (values) => {
        console.log('Finish:', values);
    };

    return (
        <main className="home">
            <section>
                <p className="title">Fake Store Assessment</p>
                <div className="card">
                    <Search className="search-bar" placeholder="Search By Name" onSearch={onSearch} style={{width:"25%"}}/>
                    <Form onFinish={onFinish}>
                        <Form.Item name="min" style={{ width: '20%', marginRight: '10px'}} placeholder="Minimum Product Range" />
                        <Form.Item name="max" style={{ width: '20%', marginRight: '10px'}} placeholder="Maximum Product Range"  />
                        <Button type="primary" htmlType="submit">Submit</Button>
                    </Form>
                    <Card className="card-grid" title="Card Title">
                        {SearchFilterMap(product, searchParam)}
                    </Card>
                </div>
            </section>
        </main>
    )
}
