import React, { useState, useEffect } from 'react'

import Card from 'antd/lib/card'
import Empty from 'antd/lib/empty'
import Input from 'antd/lib/input'
import Form from 'antd/lib/form'
import Button from 'antd/lib/button'
const { Search } = Input

export default function Home() {
    const [product, setProduct] = useState([])
    const [searchParam, setParam] = useState('')
    const [minNum, setMin] = useState(0)
    const [maxNum, setMax] = useState(1000)

    const SearchFilterMap = (item, searchParam, minRange, maxRange) => {
        if (item.length === 0) {
            return (
                <Card.Grid>
                    <Empty />
                </Card.Grid>
            )
        }
        console.log(maxRange)
        return item.map((item, index) => {
            if ((item.title.toLowerCase()).includes(searchParam.toLowerCase())) {
                if(item.price >= minRange && (item.price <= maxRange || maxRange === 0)) {
                        return (
                            <Card.Grid key={index}>
                                <img style={{ width: "100%" }} alt="example" src={item.image} />
                                <p><strong>{item.title}</strong></p>
                                <p>{"$" + item.price.toFixed(2)}</p> {/* Integer must have 2 decimal spaces to look better */}
                                <p>{item.category.charAt(0).toUpperCase() + item.category.slice(1)}</p> {/* Capitalize First Letter */}
                                <p>{item.description}</p>
                            </Card.Grid>
                        )
                } else {return null}
            } else {
                return null
            }
        })
    }

    useEffect(() => {
        fetch('https://fakestoreapi.com/products/')
            .then(res => res.json())
            .then((productsArray) => {
                setProduct(productsArray)
            })
    }, [])

    const onSearch = value => setParam(value);

    const onFinish = (values) => {
        console.log(values)
        if (values.min === undefined || values.min === "") {
            setMin(0)
        }
        if (values.max === undefined || values.max === "") {
            setMax(0)
        }
        setMin(Number(parseFloat(values.min)))
        setMax(Number(parseFloat(values.max)))
    };

    return (
        <main className="home">
            <section>
                <p className="title">Fake Store Assessment</p>
                <div className="card">
                    <Search className="search-bar" placeholder="Search By Name" onSearch={onSearch} style={{ width: "25%" }} />
                    <Form name="horizontal_login" layout="inline" onFinish={onFinish}>
                        <Form.Item name="min">
                            <Input placeholder="Minimum Product Range" />
                        </Form.Item>
                        <Form.Item name="max">
                            <Input placeholder="Maximum Product Range" />
                        </Form.Item>
                        <Form.Item placeholder="Maximum Product Range" />
                        <Button type="primary" htmlType="submit">Submit</Button>
                    </Form>
                    <Card className="card-grid" title="Card Title">
                        {SearchFilterMap(product, searchParam, minNum, maxNum)}
                    </Card>
                </div>
            </section>
        </main>
    )
}
